import { Card } from "react-bootstrap";

const Carde = (props) => {


    const check = (event)=>{
        console.log(event.target.Card);
     
    }
   

    return (
        
        <>
             <a className="cards" style={{ textDecoration: "none", color: "black" }} href={props.path} onMouseDown={check}>
            <Card style={{ width: '12rem', border: "none", textAlign: "center" }} >
               
                    <Card.Img variant="top" src={props.imgsrc} />
                    <Card.Body>
                        <Card.Title>{props.title}</Card.Title>
                        <Card.Text>
                            {props.description}
                        </Card.Text>
                    </Card.Body>
    
            </Card>
            </a>
        </>
    )
}



export default Carde;
