import React from "react";



const Productview = (props) => {

    const queryParams = new URLSearchParams(window.location.search);
    const imgsrc = queryParams.get('imgsrc');
    const title = queryParams.get('title');
    const description = queryParams.get('description');
    console.log(title);
    console.log(description);
    return (

        <>
            <h1>this is product view page</h1>
            <div className="container" style={{width:"80%", margin:"auto", display:"flex", justifyContent:"center", flexDirection:"column"}}>

                <img src={imgsrc} style={{width:"60%", margin:"auto", display}} alt="not found" />

                <div>
                    <h1>{title}</h1>
                    <br />
                    <h3>{description}</h3>
                </div>
            </div>

        </>
    )
}

export default Productview;
