import React,{useState,useEffect} from 'react';
import {Form,Button} from "react-bootstrap";
const Register = () => {
    const[name ,setName] = useState("");
    const[email ,setEmail] = useState("");
    const[pass ,setPass] = useState("");
    
    
   
    const register = ()=>{
        
      
       
        
        let cred = {
            name: name,
            email: email,
            password: pass
        }
      fetch("http:/register",{method: "POST",headers: {'Content-Type': 'application/json'},body: JSON.stringify(cred)}).then(res => res.json()).then(data => console.log(data) );

    }
    return (
        <>
            <Form className="form-container w-75 mt-5 m-auto "  onSubmit={register}>
            <Form.Group className="mb-2 m-2" controlId="formBasicName">
                    <Form.Label>Name</Form.Label>
                    <Form.Control type="text" placeholder="Enter name"  name="name" onChange={(e)=>{setName(e.target.value)}} value={name}/>
                </Form.Group>
                <Form.Group className="mb-2 m-2" controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="Enter email" name="email" onChange={(e)=>{setEmail(e.target.value)}} value={email}/> 
                </Form.Group>
                <Form.Group className="mb-3 m-2" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" name="password" onChange={(e)=>{setPass(e.target.value)}} value={pass}/> 
                </Form.Group>

                <Button variant="success" style={{ textAlign: "center", margin: "auto", display: "flex" }} type="submit">
                    SignUp
                </Button>
            </Form>
        </>
    )
}


export default Register;
