import React from "react";
import Carde from "../component/Card";
import "bootstrap/dist/css/bootstrap.min.css";
import Slider from "../component/Slider";
import productlist from "../products.json";

const Home = async() => {
   let product =  await fetch("http://localhost:3001/api/products").then((response) => {return response.json}).then(data => data.products);
    return (
        <>
            <Slider />
            <br />
            <h1 className=" container text-center">Trending Products</h1>
            <br />
            <div className=" trend-prod container fluid" style={{width: '90%', minHeight: '30vh',textAlign: "center", display: 'flex',justifyContent:"space-between", margin:"auto", flexWrap:"wrap"}}>
            {product.map((prod , i)=>{
            return <Carde imgsrc={prod.imgsc.toString()} title={product.title.toString()} description={product.desc.toString()} />
               
            }
        )}
        </div>
  
        </>
    )
}

export default Home;
