import { Card } from "react-bootstrap";


const Carde = (props) => {

    const url = " ";
    const check = (event)=>{
       url = event.target.parentElement.href;
     console.log(url.toStrings);
   
    }
   
    props.myurl(url);
    return (
        
        <>
             
            <Card style={{ width: '12rem', border: "none", textAlign: "center" }} onMouseEnter={check} >
            <a className="cards" style={{ textDecoration: "none", color: "black" }} href={props.path}>
                    <Card.Img variant="top" src={props.imgsrc} />
                    <Card.Body>
                        <Card.Title>{props.title}</Card.Title>
                        <Card.Text>
                            {props.description}
                        </Card.Text>
                    </Card.Body>
                    </a>
            </Card>
          
        </>
    )
}



export default Carde;
