import { Card} from "react-bootstrap";
import {Productview} from "../pages/Productview";
const Carde = (props) => {
    
    props.sendurl(props.path);

    return (
        
        <>

            <Card style={{ width: '12rem', border:"none" , textAlign:"center"}} onClick={sendurl}>
                <a className="cards" style={{textDecoration:"none", color:"black"}} href={props.path}>
                    <Card.Img variant="top" src={props.imgsrc} />
                    <Card.Body>
                        <Card.Title>{props.title}</Card.Title>
                        <Card.Text>
                            {props.description}
                        </Card.Text>
                    </Card.Body>
                </a>
            </Card>
            
                   </>
    )
}

export default Carde;
