import React from "react";
import Carde from "../component/Card";
import "bootstrap/dist/css/bootstrap.min.css";
import Slider from "../component/Slider";


const Home = () => {

    const [data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("http://localhost:7777/api/products")
      .then((res) => res.json())
      .then((data) => setData(data.message[2]));
  }, []);
  
    return (
        <>
            <Slider />
            <br />
            <h1 className=" container text-center">Trending Products</h1>
            <br />
            <div className=" trend-prod container fluid" style={{width: '90%', minHeight: '30vh',textAlign: "center", display: 'flex',justifyContent:"space-between", margin:"auto", flexWrap:"wrap"}}>
            {  fetch("http://localhost:7777/api/products").then(response => response.json).then(result => {
                 return <Carde imgsrc={data.message.imgsc.toString()} title={data.message.title.toString()} description={data.message.desc.toString()} />
            }
           
               
            
        )}
        </div>
  
        </>
    )
}

export default Home;
