import { Card } from "react-bootstrap";

const Carde = (props) => {


    const check = (event)=>{
        console.log(event.target.value);
    }
   

    return (
        
        <>

            <Card style={{ width: '12rem', border: "none", textAlign: "center" }} onClick={check} >
                <a className="cards" style={{ textDecoration: "none", color: "black" }} >
                    <Card.Img variant="top" src={props.imgsrc} />
                    <Card.Body>
                        <Card.Title>{props.title}</Card.Title>
                        <Card.Text>
                            {props.description}
                        </Card.Text>
                    </Card.Body>
                </a>
            </Card>

        </>
    )
}



export default Carde;
