import 'bootstrap/dist/css/bootstrap.css';
import { Form, Button, Nav } from "react-bootstrap";

const Login = () => {
const[email ,setEmail] = useState("");
const[password ,setPassword] = useState("");


useEffect(()=>{
console.log(email);
console.log(pass);
})

const addData = ()=>{
  let cred = {
    user,
    pass
}
fetch("http://localhost:7777/login",{method: "POST",headers: {'Accept': 'application/json','Content-Type': 'application/json'},body: JSON.stringify(cred)}).then(res => res.json()).then(data => console.log(data) );

}

  return (
    <>
      <form onSubmit={addData}>
        <div className="container container-fluid">

        <div class="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" onChange={(e)=>{setUser(e.target.value)}} value ={user}/>
            <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password" onChange={(e)=>{setPass(e.target.value)}} value={pass}/>
        </div>
        </div>
       
        <button type="submit" class="btn btn-primary" >Submit</button>
      </form>
      <Nav.Link href="/register"> Create a new account</Nav.Link>

    </>
  )
}


export default Login;
