import React,{useState } from "react";
import { Card } from "react-bootstrap";
import Productview from "../pages/Productview";

const Carde = (props) => {

    const [url , seturl] = useState("");
    const check = (event)=>{
       seturl(event.target.parentElement.href);
     console.log(url);
   
    }
   
   
    return (
        
        <>
             
            <Card style={{ width: '12rem', border: "none", textAlign: "center" }} onMouseEnter={check} >
            <a className="cards" style={{ textDecoration: "none", color: "black" }} href={props.path}>
                    <Card.Img variant="top" src={props.imgsrc} />
                    <Card.Body>
                        <Card.Title>{props.title}</Card.Title>
                        <Card.Text>
                            {props.description}
                        </Card.Text>
                    </Card.Body>
                    </a>
            </Card>
            <Productview imgsrc={check}/>
          
        </>
    )
}



export default Carde;
